import { isNumber } from '../util';
import { throwIfNullOrUndefined } from '../throwHelper';

/** @module */

/**
 * <h3> min() â‡’ number|undefined </h3>
 * Returns the smallest of all elements of the vector.
 * @returns {number|undefined}  The minimum number if the vector contains number; otherwise, undefined.
 * @example
 * const vec = Vec.of(1, 2, 3, 4, 5, 0);
 * const min = vec.min();
 * console.log(min);
 * // => 0
 */
function min() {
  throwIfNullOrUndefined(this, 'this');

  if (this.length === 0) {
    return undefined;
  }
  let minNum;

  for (let i = 1; i < this.length; i += 1) {
    if (isNumber(this[i])) {
      if (minNum === undefined) {
        minNum = this[i];
      } else if (this[i] < minNum) {
        minNum = this[i];
      }
    }
  }

  return minNum;
}

export default min;
