import { isNumber } from '../util';
import { throwIfNullOrUndefined } from '../throwHelper';

function min() {
  throwIfNullOrUndefined(this, 'this');

  if (this.length === 0) {
    return undefined;
  }
  let minNum;

  for (let i = 1; i < this.length; i += 1) {
    if (isNumber(this[i])) {
      if (minNum === undefined) {
        minNum = this[i];
      } else if (this[i] < minNum) {
        minNum = this[i];
      }
    }
  }

  return minNum;
}

export default min;
